import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const NumberBox = () => {
  const [pin, setPin] = useState('');

  useEffect(() => {
    if (pin.length === 4) {
      // TODO: Perform network check with pin
      console.log(`Checking pin ${pin}...`);
    }
  }, [pin]);

  const handleNumberClick = (digit) => {
    setPin((currentPin) => currentPin.length < 4 ? currentPin + digit : currentPin);
  };

  const isTyped = (index) => index < pin.length;

  return (
    <Container>
      <TypedNumberContainer>
        {Array.from({ length: 4 }, (_, index) => (
          <TypedNumber key={index} typed={isTyped(index)} />
        ))}
      </TypedNumberContainer>
      <NumberContainer>
        {Array.from({ length: 10 }, (_, index) => (
          <Number key={index} onClick={() => handleNumberClick(index)}>
            {index}
          </Number>
        ))}
      </NumberContainer>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const NumberContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 100px);
  grid-template-rows: repeat(4, 100px);
  box-sizing: border-box;
  row-gap: 4px;
  column-gap: 4px;
`;

const TypedNumberContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 16px;
  margin-bottom: 32px;
`;

const TypedNumber = styled.div`
  width: 20px;
  height: 20px;
  background-color: ${({ typed }) => typed ? 'green' : 'gray'};
  border: solid 1px ${({ typed }) => typed ? 'oragane' : 'gray'};
  border-radius: 50%;
  margin-right: 8px;
`;

const Number = styled.button`
  border-radius: 50%;
  background-color: white;
  color: orange;
  font-size: 24px;
  font-weight: bold;
  transition: background-color 0.3s;

  &:hover {
    background-color: lightgray;
  }
`;

export default NumberBox;
